import React, {useState, useEffect} from 'react'
import { Button, Typography , Divider, Row,Col, Modal,Layout, Menu } from 'antd';
import { BookOutlined, UserOutlined, VideoCameraOutlined } from '@ant-design/icons';
import Fade from 'react-reveal/Fade'
import {Link} from 'react-router-dom'
import { useSelector } from 'react-redux';
import VideoUpload from '../../../utils/VideoUpload';
import FileUpload from '../../../utils/FileUpload';
import './Mypage.css'

const { Content, Sider } = Layout;
const { Title, Paragraph , Text} = Typography;

const MyPage = () => {
    const user = useSelector((state) => state.user);
    const [path, setPath] = useState('')
    const [visiblewebcam, setVisiblewebcam] = useState(false)
    const [visiblefile, setVisiblefile] = useState(false)
    const [start, setStart] = useState(true);
    const [video, setVideo] = useState('');

      const saveVideoHandler = (faceVideo) => {
        setVideo(faceVideo);
      };
    useEffect(() => {
        setPath(`http://localhost:5000/uploads/${user.userData.studentId}.webm`)
    }, [video])

    const ShowWebcamHandler = () =>{
        setVisiblewebcam(true)
    }
    const SaveWebcamHandler = () => {
        setVisiblewebcam(false)
      };
    const ShowFileHandler = () =>{
        setVisiblefile(true)
    }
    const SaveFileHandler = () => {
        setVisiblefile(false)
      };

    return (
    <Fade>
        <Layout>
        <Sider className="site-layout-background" 
        breakpoint="xs"
        collapsedWidth="0" 
        onBreakpoint={broken => {
        console.log(broken);
      }}
        onCollapse={(collapsed, type) => {
        console.log(collapsed, type);
      }}> 
      <div className='logo'>
        <Menu
          mode="inline"
          defaultSelectedKeys={['1']}
          style={{ height: '100%', borderRight: 0}}
        >
            <Menu.Item key="1"  icon={<VideoCameraOutlined />}>등록영상 확인</Menu.Item>
            <Menu.Item key="2" icon={<UserOutlined />} >출석체크 확인</Menu.Item>
            <Menu.Item key="3" icon={<BookOutlined />}>등록수업 확인</Menu.Item>
        </Menu>
        </div>
      </Sider>
        <Content>
        <div className='contents' style={{paddingTop:'2rem'}}>
        <Title >마이페이지</Title>
        <Divider/>

        <Title level={5}>등록 영상</Title>
            <Row justify='center'>
                <Col span={24} >
                    <video controls height="230" >
                        <source src ={path} type="video/webm" />
                    </video>
                    <br/>
                    <div style={{display:'flex'}}>
                    <Button onClick={ShowWebcamHandler}>웹캠으로 변경하기</Button>
                    <Modal
                    title="웹캠으로 등록하기"
                    centered
                    visible={visiblewebcam}
                    footer={null}
                    onCancel={SaveWebcamHandler}
                    width={700}
                    >
                        <div style={{display:'flex',flexDirection:'column', alignItems:'center'}}>
                        <VideoUpload updateStartHandler={SaveWebcamHandler} saveVideoHandler={saveVideoHandler}/>
                        </div>
                    </Modal>
                    <div style={{marginLeft:'1rem'}}>

                    <Button onClick={ShowFileHandler}>파일로 변경하기</Button>
                    <Modal
                    title="파일로 등록하기"
                    centered
                    visible={visiblefile}
                    footer={null}
                    onCancel={SaveFileHandler}
                    width={700}
                    >
                        <div style={{display:'flex',flexDirection:'column', alignItems:'center'}}>
                        <FileUpload updateStartHandler={SaveFileHandler} saveVideoHandler={saveVideoHandler}/>
                        </div>
                    </Modal>
                    </div>
                    </div>
                </Col>
                <Col span={24}>

                </Col>
            </Row>
            <Divider/>
            <Title level={5}>출석체크 확인</Title>
            <Row justify='center'>
                <p>출첵확인</p>
                <Col>

                </Col>
            </Row>
        </div>
        </Content>
        </Layout>
    </Fade>
    )
}

export default MyPage
