import React, {useState, useEffect} from 'react'
import { Button, Typography , Divider, Row,Col, Image, Spin } from 'antd';
import Fade from 'react-reveal/Fade'
import {Link} from 'react-router-dom'
import { useSelector } from 'react-redux';


const { Title, Paragraph , Text} = Typography;

const MyPage = () => {
    const user = useSelector((state) => state.user);
    const [path, setPath] = useState('')

    useEffect(() => {
        setPath(`http://localhost:5000/uploads/${user.userData.studentId}.webm`)

    }, [])

    return (
    <Fade>
        <div className='contents'>
        <Title level={3}>등록 영상</Title>
            <Row>
                <Col span={12} offset={6}>
                    <video controls height="230" >
                        <source src ={path} type="video/webm" />
                    </video>
                    <Button type='primary'>변경하기</Button>
                </Col>
            </Row>
            <Divider/>
        </div>
    </Fade>
    )
}

export default MyPage
